if (localStorage.productTitle) {
  document.getElementById('page-title').innerHTML = localStorage.productTitle;
}

if (localStorage.productCategory) {
  document.getElementById('category').innerHTML = localStorage.productCategory;
}

if (localStorage.productPrice) {
  document.getElementById('price').innerHTML = localStorage.productPrice;
}

if (localStorage.productDescription) {
  document.getElementById('description').innerHTML = localStorage.productDescription;
}
